mapper.insert:
	INSERT 
	  INTO 
	       CL_ACTIVITY_BOARD 
	       (
	       ACTIVITY_NO
	     , ACTIVITY_TITLE
	     , ACTIVITY_CONTENT
	     , LAT
	     , LNG
	     , MEMBER_NO
	     , REGION_NO
           ) 
    VALUES 
    	   (
           SEQ_ACTIVITY_NO.NEXTVAL
         , #{title}
         , #{content}
         , #{lat}
         , #{lng}
         , #{memberNo}
         , #{regionNo}
		   )

	INSERT 
	  INTO 
	       CL_ACTIVITY_ATTACHMENT 
	       (
           FILE_NO
         , REF_BNO
         , ORIGIN_NAME
         , CHANGE_NAME
         , FILE_PATH
           ) 
    VALUES 
           (
           SEQ_ACTIVITY_FILE_NO.NEXTVAL
         , #{refBno}
         , #{originName}
         , #{changeName}
         , #{filePath}
		   )
