mapper.insert:

    		SELECT SEQ_ACTIVITY_NO.NEXTVAL FROM DUAL

	INSERT 
	  INTO 
	       CL_ACTIVITY_BOARD 
	       (
	       ACTIVITY_NO
	     , ACTIVITY_TITLE
	     , ACTIVITY_CONTENT
	     , LAT
	     , LNG
	     , VIEW_COUNT
	     , STATUS
	     , ENROLL_DATE
	     , MEMBER_NO
	     , REGION_NO
           ) 
    VALUES 
    	   (
           #{activityNo}
         , #{title}
         , #{content}
         , #{lat}
         , #{lng}
         , DEFAULT
         , 'Y'
         , SYSDATE
         , #{memberNo}
         , #{regionNo}
		   )

	INSERT 
	  INTO 
	       CL_ACTIVITY_ATTACHMENT 
	       (
           FILE_NO
         , REF_BNO
         , ORIGIN_NAME
         , CHANGE_NAME
         , FILE_PATH
         , UPLOAD_DATE
           ) 
    VALUES 
           (
           SEQ_ACTIVITY_FILE_NO.NEXTVAL
         , #{refBno}
         , #{originName}
         , #{changeName}
         , #{filePath}
         , SYSDATE
		   )

INSERT INTO CL_ACTIVITY_CERTIFICATION (
    ACTIVITY_BOARD_NO,
    CERTIFICATION_NO
) VALUES (
    #{activityNo},
    #{certificationNo}
)
