mapper.insert:

    SELECT SEQ_CAMPAIGN_NO.NEXTVAL FROM DUAL

INSERT 
  INTO 
       CL_CAMPAIGN 
       (
       CAMPAIGN_NO
     , CAMPAIGN_TITLE
     , CAMPAIGN_CONTENT
     , CATEGORY_NO
     , START_DATE
     , END_DATE
     , MEMBER_NO
	   ) 
VALUES 
	   (
       #{campaignNo}
     , #{campaignTitle}
     , #{campaignContent}
     , #{categoryNo}
     , #{startDate}
     , #{endDate}
     , #{memberNo}
	   )

insert.foreach:
 INSERT 
   INTO 
        CL_CAMPAIGN_ATTACHMENT
 	   (
        FILE_NO
      , REF_BNO
      , ORIGIN_NAME
      , CHANGE_NAME
      , FILE_LEVEL
      , FILE_PATH
      , UPLOAD_DATE
        )
VALUES
        (
        SEQ_CAMPAIGN_FILE_NO.NEXTVAL
      , #{item.refBno}
      , #{item.originName}
      , #{item.changeName}
      , #{item.fileLevel}
      , #{item.filePath}
      , SYSDATE
        )
